// library
import React from 'react';

// import
import classes from './App.module.css';

// Component
import Header from './Components/Header/Header';
import Commander from './Components/Commander/Commander';

// Redux
import { connect } from 'react-redux';

function App(props) {
  let hystory;

  if(props.hystory && props.hystory != '' ) {
    hystory = props.hystory.map(result => ( 
      <div
        key={result.id} className={classes.result}
      >

      </div>
    ))
  }

  return (
    <div className={classes.App}>
      <Header />

      <div className='container'>
        <div className={classes.content}>
          <h1>À la conquête du monde</h1>
          <div className={classes.minions}>
            <span>{props.minions}</span>
            minions infiltrés
          </div>
        </div>

        <Commander />

        {
          props.hystory && props.hystory != '' ?
          <div className={classes.content}>
            <h2>Tableau des infiltrations</h2>
            {hystory}
          </div>
          : null
        }
      </div>
    </div>
  );
}

// mapStateToProps
const mapStateToProps = state => {
  return {
    minions: state.minion.minions,
    history: state.save.history,
  };
};

export default connect(mapStateToProps)(App);
